def move_zeros_to_left(nums):
    r_ptr, w_ptr = len(nums) - 1, len(nums) - 1

    while r_ptr >= 0:

        if nums[r_ptr]:
            nums[w_ptr] = nums[r_ptr]
            w_ptr -= 1

        r_ptr -= 1

    while w_ptr >= 0:
        nums[w_ptr] = 0
        w_ptr -= 1


import unittest


class TestMoveZeroesToLeft(unittest.TestCase):
    def test_move_zeroes_to_left(self):
        nums = [1, 0, 3, 5, 0, 0, 2, 0]
        move_zeros_to_left(nums)
        self.assertEquals(nums, [0, 0, 0, 0, 1, 3, 5, 2])
