from linked_list.linked_list import *


def reverse(head):
    first = head
    second = head.next

    first.next = None

    while second:
        tmp = second
        second = second.next
        tmp.next = first
        first = tmp

    return first


import unittest


class TestRevLinkedList(unittest.TestCase):
    def test_rev_ll(self):
        ll = LinkedList()
        ll.create_linked_list([29, 23, 82, 11, 4, 3, 21])
        rev_ll = LinkedList()
        rev_ll.head = reverse(ll.head)
        self.assertEquals(str(rev_ll), "[21, 3, 4, 11, 82, 23, 29]")
