import copy


def print_all_sum_rec(target, output, result, current_sum, start):
    if current_sum is target:
        output.append(copy.copy(result))

    for i in range(start, target):
        temp = current_sum + i

        if temp <= target:
            result.append(i)
            print_all_sum_rec(target, output, result, temp, i)
            result.pop()


def print_all_sum(target):
    output = []
    result = []

    print_all_sum_rec(target, output, result, 0, 1)

    return output


import unittest


class TestPrintAllSum(unittest.TestCase):
    def test_all_sum(self):
        self.assertEquals(print_all_sum(3), [[1, 1, 1], [1, 2]])
        self.assertEquals(print_all_sum(4), [[1, 1, 1, 1], [1, 1, 2], [1, 3], [2, 2]])
