def is_palindrome(sub_str):
    return len(sub_str) > 1 and sub_str[::-1] == sub_str


def find_all_palindrome_substrings(input_string):
    palindromes = []

    for i in range(len(input_string)):
        for j in range(i + 1, len(input_string)):
            cur_str = input_string[i : j + 1]
            if is_palindrome(cur_str):
                palindromes.append(cur_str)

    return palindromes


import unittest


class TestPalindromeSubStr(unittest.TestCase):
    def test_palindrome_sub_str(self):
        self.assertEquals(
            find_all_palindrome_substrings("aabbbaa"),
            ["aa", "aabbbaa", "abbba", "bb", "bbb", "bb", "aa"],
        )
