def find_buy_sell_stock_prices(prices):

    if not prices or len(prices) < 2:
        return None

    cur_profit = float("-inf")
    cur_buy = prices[0]
    global_sell = prices[1]
    global_profit = global_sell - cur_buy

    for i in range(1, len(prices)):
        cur_profit = prices[i] - cur_buy

        if cur_profit > global_profit:
            global_profit = cur_profit
            global_sell = prices[i]

        if cur_buy > prices[i]:
            cur_buy = prices[i]

    return global_sell - global_profit, global_sell


import unittest


class TestStockProfit(unittest.TestCase):
    def test_stock_profit(self):
        prices = [1, 2, 3, 4, 3, 2, 1, 2, 5]

        self.assertEquals(find_buy_sell_stock_prices(prices), (1, 5))
