def reverse_words(sentence):
    print(sentence)
    rev_sen = sentence[::-1]
    rev_sen_list = rev_sen.split(" ")
    output = []
    for rev_word in rev_sen_list:
        output.append(rev_word[::-1])

    return " ".join(output)


import unittest


class TestReverseWords(unittest.TestCase):
    def test_reverse_words(self):
        self.assertEquals(reverse_words("Hello, World"), "World Hello,")
        self.assertEquals(
            reverse_words("The quick brown fox jumped over the lazy dog."),
            "dog. lazy the over jumped fox brown quick The",
        )
