def can_segment_string(input_string, dictionary):

    for i in range(1, len(input_string) + 1):
        first_string = input_string[0:i]

        if first_string in dictionary:
            second_string = input_string[i:]

            if not second_string or second_string in dictionary:
                return True

            if can_segment_string(second_string, dictionary):
                return True

    return False


import unittest


class TestCanSegmentString(unittest.TestCase):
    def test(self):
        self.assertTrue(
            can_segment_string("applepie", ["apple", "pear", "pier", "pie"])
        )

        self.assertFalse(
            can_segment_string("applepeer", ["apple", "pear", "pier", "pie"])
        )

        self.assertTrue(
            can_segment_string(
                "educative.io", ["educative", "learning", ".io", "platform"]
            )
        )

        self.assertTrue(
            can_segment_string(
                "learningplatform", ["educative", "learning", ".io", "platform"]
            )
        )

        self.assertFalse(
            can_segment_string("catsandog", ["cats", "dog", "sand", "and", "cat"])
        )
